import React,{useState} from 'react'
import { View, Text,ScrollView } from 'react-native'
import { useStateValue } from "../StateProvider";
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import {Image,Button} from "react-native-elements"

const SingleScreen = () => {
    const [{ basket ,single}, dispatch] = useStateValue();
    const [id,setId] = useState(single.id);
    const [title,setTitle] = useState(single.title)
    const [image,setImage] = useState(single.image)
    const [price,setPrice] = useState(single.Price)
    
    const addToBasket = () => {
        // dispatch item into data layer
        dispatch({
          type: "ADD_TO_BASKET",
          item: {
            id: id,
            title: title,
            image: image,
            price: price,
          
          },
        });
        console.log(basket)
      };
    return (
        <View style={{flex:1,}}>
            <View style={{display:"flex",flexDirection:"row",padding:10,backgroundColor:"#00B8FA"}}>
                <Text style={{padding:10,fontWeigh:'700',color:'white',fontSize:32}}>{single.title}</Text>
            <View style={{marginLeft:'auto', marginTop:10}}>
                <MaterialCommunityIcons name="cart" color={"white"} size={26} />
               </View>
            </View>
           <ScrollView>
           <Image source={{uri:single.image}}
            style={{height: 400 }}/>
            <View style={{padding:10,fontSize:22,backgroundColor:'white',marginTop:10,}}>
            <Text >
            ${single.price}
            </Text>
            </View>
            <View style={{padding:10,fontSize:22,backgroundColor:'white',marginTop:10,}}>
            <Text style={{padding:10,fontSize:22,backgroundColor:'white',marginTop:'10'}}>
                Available in small,Medium and Large , Click add to Cart to buy this product
            </Text>
            </View>
            <Button
      buttonStyle={{borderRadius: 0, marginLeft: 0, marginRight: 0, marginTop: 110}}
      title='VIEW NOW' onPress={addToBasket} />
      <Text style={{marginBottom: 0}}></Text>
      </ScrollView>
        </View>
    )
}

export default SingleScreen
