import React,{useState,useEffect} from 'react'
import { View, Text,StatusBar,ScrollView,FlatList } from 'react-native'
import { db,storage } from "../firebase";
import Categories from "../components/Categories"
import data from "../data/category"


const HomeScreen = () => {

  const[product,setProduct] = useState([])
  useEffect(()=>{
    db.collection('products').onSnapshot((snapshot)=>{
      setProduct(snapshot.docs.map((doc)=>doc.data()))
    
  })
  },[])
  
 console.log(product)
    return (
        <View style={{flex:1 }}>
          <View style={{display:'flex',flexDirection:"row"}}>
          <Text style={{fontWeight:'700',fontSize:18,marginLeft:15}}>Categories</Text>
          <Text style={{fontWeight:'600',fontSize:16,textAlign:"right",color:'#42EE0C'}}>View All</Text>
          </View>
         <ScrollView>
        <FlatList
        data={data}
        horizontal={true}
        noOfColumns={1}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => 
        <View>
            <Categories title={item.title} image={item.image}/>
            
        </View>}
     />
     </ScrollView>
        </View>
      );
    }

export default HomeScreen
