import React,{useState} from 'react'
import { View, Text,StatusBar,ScrollView,FlatList } from 'react-native'
import NavBar from "../components/NavBar"
import { Image } from 'react-native-elements';
import Card from "../components/Card"
import { useStateValue } from "../StateProvider";

const HomeScreen = () => {
  const [{ basket, user }] = useStateValue();
console.log(basket)
const addToBasket = () => {
  // dispatch item into data layer
  dispatch({
    type: "ADD_TO_BASKET",
    item: {
      id: id,
      title: title,
      image: image,
      price: price,
    },
  });
};
   const women =[{"id":"1","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/428338/pexels-photo-428338.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"},
   {"id":"2","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/6069563/pexels-photo-6069563.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"}
   ,{"id":"3","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/5710155/pexels-photo-5710155.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"},
   {"id":"4","title":'shirt',"price":"10","image":" https://images.pexels.com/photos/6896197/pexels-photo-6896197.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"}]

   const men =[{"id":"5","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/428340/pexels-photo-428340.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"},
   {"id":"6","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/5175900/pexels-photo-5175900.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"}
   ,{"id":"7","title":'shirt',"price":"10","image":"https://images.pexels.com/photos/6238001/pexels-photo-6238001.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"},
   {"id":"8","title":'shirt',"price":"10","image":" https://images.pexels.com/photos/6238167/pexels-photo-6238167.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"}]

   const imgUrl = "https://images.pexels.com/photos/235922/pexels-photo-235922.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"

  
   
    return (
        <View style={{flex:1 }}>
          <StatusBar barStyle="light"/>
          <NavBar/>
          <ScrollView>
          <Image
            source={{ uri: imgUrl}}
            style={{height: 200 }}
            />
          <Text style={{fontWeight:'700',fontSize:18,}}>Women</Text>
          <ScrollView>
        <FlatList
        data={women}
        horizontal={true}
        noOfColumns={1}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => 
        <View>
            <Card title={item.title} image={item.image} price={item.price}/>
            
        </View>}
     />
     </ScrollView>
     <Text style={{fontWeight:'700',fontSize:18,}}>Men</Text>
     <ScrollView>
        <FlatList
        data={men}
        horizontal={true}
        noOfColumns={1}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => 
        <View>
            <Card title={item.title} image={item.image} price={item.price}/>
            
        </View>}
     />
     </ScrollView>
     </ScrollView>
        </View>
      );
    }

export default HomeScreen
