import React,{useState} from 'react'
import { View, Text,StatusBar,ScrollView,FlatList } from 'react-native'
import NavBar from "../components/NavBar"
import { Image } from 'react-native-elements';
import Card from "../components/Card"

const HomeScreen = () => {
   const data =[{"title":'shirt',"price":"10",image:"https://images.pexels.com/photos/428338/pexels-photo-428338.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"},
   {"title":'shirt',"price":"10","image":"https://images.pexels.com/photos/6069563/pexels-photo-6069563.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"}
   ,{"title":'shirt',"price":"10","image":"https://images.pexels.com/photos/5710155/pexels-photo-5710155.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"}]

   const imgUrl = "https://images.pexels.com/photos/235922/pexels-photo-235922.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"

   
    return (
        <View style={{flex:1 }}>
          <StatusBar barStyle="light"/>
          <NavBar/>
          <ScrollView>
          <Image
            source={{ uri: imgUrl}}
            style={{height: 200 }}
            />
          <Text>Women</Text>
          <ScrollView>
        <FlatList
        data={data}
        horizontal={true}
        noOfColumns={1}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => 
        <View>
            <Card title={item.title} image={item.image} price={item.price}/>
            
        </View>}
     />
     </ScrollView>
     </ScrollView>
        </View>
      );
    }

export default HomeScreen
