import React from 'react'
import { View, Text,Pressable } from 'react-native'
import { Card, ListItem, Button, Icon ,Image} from 'react-native-elements'
import { useStateValue } from "../StateProvider";

const Cards = ({title,image,price,id,navigation}) => {
  const [{ basket ,single}, dispatch] = useStateValue();

const addToBasket = () => {
  // dispatch item into data layer
  dispatch({
    type: "Set_Single",
    item: {
      id: id,
      },
  });
  navigation.navigate("single")
};
const addToCart = () => {
  // dispatch item into data layer
  dispatch({
    type: "ADD_TO_BASKET",
    item: {
      id: id,
      title: title,
      image: image,
      price: price,
     
    },
  });
};
    
    return (
        <View>
          <Card containerStyle={{width:200}}>
            <Pressable onPress={addToBasket}>
  <Card.Title>{title}</Card.Title>
  
  <Card.Divider/>
  <Card.Image  source={{ uri: image}}>
    
  </Card.Image>
  <Button
      buttonStyle={{borderRadius: 0, marginLeft: 0, marginRight: 0, marginTop: 10}}
      title='Add to Cart' onPress={addToCart} />
  </Pressable>
</Card>
        </View>
    )
}

export default Cards
