import React,{useState,useEffect} from 'react'
import { View, Text,StatusBar,ScrollView,FlatList } from 'react-native'
import NavBar from "../components/NavBar"
import axios from "axios"
import { Image } from 'react-native-elements';
import Card from "../components/Card"
import { useStateValue } from "../StateProvider";


const HomeScreen = ({navigation}) => {
  const [{ basket ,single}, dispatch] = useStateValue();
  console.log(basket)
  const[men,setMen]=useState([]);
  useEffect(() => {
    axios.get("http://localhost:4000/post/get").then(response=>{
      setMen(response.data)
    }).catch(error=>{
      console.log(error.message)
    })
    console.log(men)
}, [])
  
  

   const imgUrl = "https://images.pexels.com/photos/235922/pexels-photo-235922.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
 const imgUrl1 ="lets-go/assets/images/d1.jpg"
  
   
    return (
        <View style={{flex:1 }}>
          <StatusBar barStyle="light"/>
          <NavBar/>
          <ScrollView>
          <Image
            source={{ uri: imgUrl1}}
            style={{height: 200 }}
            />
          <Text style={{fontWeight:'700',fontSize:18,}}>Women</Text>
          <ScrollView>
        <FlatList
        data={men}
        horizontal={true}
        noOfColumns={1}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => 
        <View>
            <Card title={item.title} image={item.image} price={item.price} id={item._id} navigation={navigation}/>
            
        </View>}
     />
     </ScrollView>
     
     </ScrollView>
        </View>
      );
    }

export default HomeScreen
