import React,{useState} from 'react'
import { View, Text,StatusBar,ScrollView,FlatList } from 'react-native'
import NavBar from "../components/NavBar"
import { Image } from 'react-native-elements';
import Card from "../components/Card"

const HomeScreen = () => {
   const data =[{"title":'shirt',"image":require("../assets/running1.jpg")},{"title":'shirt',"image":require("../assets/running1.jpg")},{"title":'shirt',"image":require("../assets/running1.jpg")},{"title":'shirt',"image":require("../assets/running1.jpg")},{"title":'shirt',"image":require("../assets/running1.jpg")},{"title":'shirt',"image":require("../assets/running1.jpg")}]
    return (
        <View style={{ flex: 1}}>
          <StatusBar barStyle="light"/>
          <NavBar/>
          <Image
            source={{ uri: require("../assets/running1.jpg")}}
            style={{height: 300 }}
            />
          <Text>Home!</Text>
         {/* <View style={{display:"flex",flexDirection:"row"}}>
          <Card title="Shirt" image={require("../assets/running2.jpg")} price="$20"/>
          <Card title="Shirt" image={require("../assets/running3.jpg")} price="$20"/>
          <Card title="Shirt" image={require("../assets/running4.jpg")} price="$20"/>
    </View>*/}
          <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => <View><Card title={item.title} image={item.image} price={item.price}/></View>}
     />
        </View>
      );
    }

export default HomeScreen
